import React from 'react'

export default class IfDemo extends React.Component{

    constructor(){
        super()
        this.state = {
            names: ['哈哈' , '呵呵']
        }
    }

    render(){
        const { names } = this.state
        return (
            <div>
                <h1>条件渲染</h1>
                <p>{ names }</p>
            </div>
        )
    }
}
