export default class StateComponent extends React.Component{

    constructor(props){
        super(props)

        this.state = {
            name: 10
        }
    }

    increment () {
    }

    decrement () {
        this.state.name--
    }


    render(){
        return(
            <div>
                <h1> 我是 : { this.state.name }</h1>
                <button onClick = { this.increment }>increment</button>
                <button onClick = { this.decrement }>decrement</button>
            </div>
        )
    }


}
